script:
frappe.ui.form.on("Expenses Entry", {
    custom_calculated_tax: function(frm) {
        let taxable_total = 0;

        // Sum debit of taxable rows
        (frm.doc.accounts || []).forEach(row => {
            if (row.custom_taxed) {
                taxable_total += flt(row.debit || 0);
            }
        });

        if (taxable_total <= 0) {
            frappe.msgprint(__('No taxable rows found in Accounts table.'));
            return;
        }

        if (!frm.doc.custom_tax_account) {
            frappe.msgprint(__('Please set Tax Account in Custom Tax Account field.'));
            return;
        }

        let tax_amount = flt(taxable_total * 0.15);

        // check if tax row already exists
        let tax_row = (frm.doc.accounts || []).find(r => r.account === frm.doc.custom_tax_account);

        if (tax_row) {
            // ✅ update existing row
            frappe.model.set_value(tax_row.doctype, tax_row.name, "debit", tax_amount);
            frappe.model.set_value(tax_row.doctype, tax_row.name, "credit", 0);
        } else {
            // ✅ create new row
            let child = frm.add_child("accounts");
            frappe.model.set_value(child.doctype, child.name, "account", frm.doc.custom_tax_account);
            frappe.model.set_value(child.doctype, child.name, "debit_in_account_currency", tax_amount);
            frappe.model.set_value(child.doctype, child.name, "credit", 0);
        }

        frm.refresh_field("accounts");

        frappe.show_alert({
            message: __("Calculated Tax row updated with Debit: {0}", [tax_amount]),
            indicator: "green"
        });
    }
});
